import React from "react"

class SignIn extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      signInEmail: '',
      signInPassword: '',
      rememberMe: false,
    }
  }
  componentDidMount() {
    const storedEmail = localStorage.getItem('signInEmail');
    const storedPassword = localStorage.getItem('signInPassword');
    const rememberMe = localStorage.getItem('rememberMe') === 'true';

    if (rememberMe && storedEmail && storedPassword) {
      this.setState({ email: storedEmail, password: storedPassword });
    }
    this.setState({ rememberMe });

  }

  onEmailChange = (event) =>{
    this.setState({signInEmail:event.target.value})
  } 
  onPasswordChange = (event) =>{
    this.setState({signInPassword:event.target.value})
  }
  handleRememberMeChange = (event) => {
    const rememberMe = event.target.checked;
    this.setState({ rememberMe });
    localStorage.setItem('rememberMe', rememberMe);
    if (!rememberMe) {
      localStorage.removeItem('email');
      localStorage.removeItem('password');
    }
  };
  handleSubmit = (event) => {
    event.preventDefault();
    // Access the value of rememberMe from the state and use it as needed.
    console.log('Remember Me:', this.state.rememberMe);
    console.log('Remember Me:', this.state.signInEmail);
    console.log('Remember Me:', this.state.signInPassword);

    if (this.state.rememberMe) {
      localStorage.setItem('email', this.state.email);
      localStorage.setItem('password', this.state.password);
    } else {
      localStorage.removeItem('email');
      localStorage.removeItem('password');
    }

  };

  onSumbitSignIn = () =>{
    fetch('http://localhost:3000/signin', {
      method: 'post',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({
        email: this.state.signInEmail,
        password: this.state.signInPassword
      })
    }).then(response => response.json())
    .then(user => {
      if (user.id){
        this.props.loadUser(user);
        this.props.onRouteChange('home');
      }
    })
  }
  
  render(){
    const{onRouteChange} = this.props;
    return (
      <article className="br3 ba b--black-10 mv4 w-100 w-50-m w-25-l mw6 shadow-5 center">
      <main className="pa4 black-80">
      <htmlForm className="measure" onSubmit={this.handleSubmit}>
        <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
          <legend className="f1 fw6 ph0 mh0">Sign In</legend>
          <div className="mt3">
            <label className="db fw6 lh-copy f6" htmlFor="email-address">Email</label>
            <input 
              className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
              type="email" 
              name="email-address"  
              id="email-address"
              onChange={this.onEmailChange}
              />
          </div>
          <div className="mv3">
            <label className="db fw6 lh-copy f6" htmlFor="password">Password</label>
            <input 
            className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
            type="password" 
            name="password"  
            id="password" 
            onChange={this.onPasswordChange}
            />
          </div>
          <label className="pa0 ma0 lh-copy f6 pointer"><input type="checkbox" checked={this.state.rememberMe} onChange={this.handleRememberMeChange}/> Remember me</label>
        </fieldset>
        <div className="">
          <input onClick= {this.onSumbitSignIn}
          className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" 
          type="submit" 
          value="Sign in"/>
        </div>
        <div className="lh-copy mt3">
          <p onClick={() => onRouteChange('Regsiter')} href="#0" className="f6 link dim black db pointer">Sign up</p>
        </div>
      </htmlForm>
    </main>
    </article>
  );
}}
export default SignIn;
